let dataProject = []
let i = 0


function getProject(event) {

    
    event.preventDefault()

    let projectName = document.getElementById('projectName').value
    let startDate = document.getElementById('startDate').value
    let endDate = document.getElementById('endDate').value
    let description = document.getElementById('description').value
    let nodeJs = document.getElementById('nodeJs').checked
    let nextJs = document.getElementById('nextJs').checked
    let reactJs = document.getElementById('reactJs').checked
    let typeScript = document.getElementById('typeScript').checked
    let upimg = document.getElementById('upimg').files

    upimg = URL.createObjectURL(upimg[0]);

    if (projectName == "") {
        return alert('Tolong Lengkapi Form yg tersedia ya')
    }
    else if (startDate == "") {
        return alert('Tolong Lengkapi Form yg tersedia ya')
    } else if (endDate == "") {
        return alert('Tolong Lengkapi Form yg tersedia ya')
    } else if (description == "") {
        return alert('Tolong Lengkapi Form yg tersedia ya')
    } else if (upimg == "") {
        return alert('Tolong Lengkapi Form yg tersedia ya')
    } else if (!nodeJs && !nextJs && !reactJs && !typeScript) {
        return alert('Mohon pilih teknologi yang anda butuhkan ya')
    }
    let data = { 
        projectName, 
        startDate, 
        endDate, 
        description, 
        nodeJs, 
        nextJs, 
        reactJs, 
        typeScript, 
        upimg,
        postedAt:new Date()
    }

    dataProject.push(data)
    console.log(dataProject)

    showData()

 
}


